--
-- Table structure for table `posts`
--

DROP TABLE IF EXISTS `posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `posts` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `post_title` varchar(191) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Tiêu đề bài viết',
  `post_slug` varchar(191) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Tiêu đề bài viết không dấu',
  `post_intro` text COLLATE utf8_unicode_ci COMMENT 'Mô tả ngắn bài viết',
  `post_full` longtext COLLATE utf8_unicode_ci NOT NULL COMMENT 'Nội dung bài viết',
  `posts_medias_id` int(10) unsigned DEFAULT NULL COMMENT 'Ảnh bài viêt',
  `post_status` tinyint(4) NOT NULL DEFAULT '1' COMMENT 'Trạng thái bài viết [1: Đã đăng, 2: Xét duyệt]',
  `post_format` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Định dạng bài viết [1: Chuẩn, 2: Video, 3: Audio, 4: Bộ sưu tập]',
  `post_keyword` varchar(191) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Từ khóa bài viết',
  `visit` tinyint(4) NOT NULL DEFAULT '0' COMMENT 'Lượt xem bài viết',
  `user_id` int(10) unsigned NOT NULL COMMENT 'Tác giả bài viết',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

